const {
  ReservationModel
} = require('./ReservationModel');
const {
  RestaurantModel,
  validateRestaurant
} = require('./RestaurantModel');
const {
  TableModel,
  validateTable
} = require('./TableModel');
class RestaurantBookingSystem {
  constructor() {
    // Initialize an empty object to store tables and their reservations
    this.tables = {};
  }

  // async addRestaurant(name, address, phone, hoursOfOperation) {
  //     try {
  //         const restaurantData = {
  //             name,
  //             address,
  //             phone,
  //             hoursOfOperation,
  //         };

  //         const { error } = validateRestaurant(restaurantData);
  //         if (error) {
  //             console.log('Validation error:', error.details[0].message);
  //             return;
  //         }

  //         const newRestaurant = new RestaurantModel(restaurantData);
  //         await newRestaurant.save();
  //         console.log(`Restaurant ${name} added successfully.`);
  //     } catch (error) {
  //         console.error('Error adding restaurant:', error.message);
  //     }
  // }

  async addTable(capacity, restaurantId) {
    try {
      const restaurant = await RestaurantModel.findByIdAndUpdate(restaurantId, {
        $inc: {
          tables: 1,
          capacity: capacity
        }
      }, {
        new: true
      });
      if (!restaurant) {
        console.log(`Restaurant with ID ${restaurantId} does not exist.`);
        return;
      }
      const existingTablesCount = await TableModel.countDocuments({
        restaurantId
      });
      const newTable = new TableModel({
        tableNumber: existingTablesCount + 1,
        capacity,
        restaurantId
      });
      await newTable.save();
      console.log(`Table ${newTable.tableNumber} with capacity ${capacity} added successfully.`);
    } catch (error) {
      console.error('Error adding table:', error.message);
    }
  }
  async makeReservation(restaurantId, tableId, customerName, reservationTime) {
    try {
      const restaurant = await RestaurantModel.findById(restaurantId);
      const table = await TableModel.findById(tableId);
      if (restaurant && table) {
        // Check if the reservationTime is a valid date
        const desiredDateTime = new Date(reservationTime);
        if (isNaN(desiredDateTime)) {
          console.log('Invalid date or time format.');
          return;
        }

        // Check if the table is available at the specified time
        const overlappingReservations = table.resDates.filter(resDate => desiredDateTime >= new Date(resDate) && desiredDateTime <= new Date(resDate.getTime() + 2 * 60 * 60 * 1000));
        if (overlappingReservations.length === 0) {
          // Table is available, make reservation
          console.log(reservationTime);
          table.resDates.push(reservationTime);
          await table.save();
          // Save the reservation in the ReservationModel
          const newReservation = new ReservationModel({
            restaurantId,
            tableId,
            customerName,
            reservationTime
          });
          await newReservation.save();
          console.log(reservationTime);
          console.log(`Reservation for Table ${table.tableNumber} at ${reservationTime} for ${customerName} successful.`);
        } else {
          console.log(`Table ${table.tableNumber} is already reserved at ${reservationTime}.`);
        }
      } else {
        console.log(`Restaurant or Table does not exist.`);
      }
    } catch (error) {
      console.error('Error making reservation:', error.message);
    }
  }
  async getAvailableTables(restaurantId, date, time) {
    try {
      // Validate date and time
      const reservationDateTime = new Date(`${date} ${time}`);
      if (isNaN(reservationDateTime)) {
        throw new Error('Invalid date or time format.');
      }

      // Find tables where the specified date and time are not in the resDates array
      const availableTables = await TableModel.find({
        restaurantId,
        $or: [{
          resDates: {
            $size: 0
          }
        },
        // Tables with no reservations
        {
          resDates: {
            $nin: [reservationDateTime]
          }
        } // Tables without the specified date
        ]
      });

      // Filter tables based on overlapping reservations
      const filteredTables = availableTables.filter(table => {
        const overlappingReservations = table.resDates.filter(reservationDateTime => reservationDateTime >= new Date(reservationDateTime) && reservationDateTime <= new Date(reservationDateTime.getTime() + 2 * 60 * 60 * 1000));
        return overlappingReservations.length === 0;
      });
      return filteredTables.map(table => ({
        tableNumber: table.tableNumber,
        capacity: table.capacity
      }));
    } catch (error) {
      console.error('Error getting available tables:', error.message);
      throw error;
    }
  }
}
module.exports = {
  RestaurantBookingSystem
};
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

