const { ReservationModel } = require('./ReservationModel');
const { RestaurantModel, validateRestaurant } = require('./RestaurantModel');
const { TableModel, validateTable } = require('./TableModel');

class RestaurantBookingSystem {
    constructor() {
        // Initialize an empty object to store tables and their reservations
        this.tables = {};
    }
    
    // async addRestaurant(name, address, phone, hoursOfOperation) {
    //     try {
    //         const restaurantData = {
    //             name,
    //             address,
    //             phone,
